blockchain/contracts/BatchContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title BatchContract
 * @dev Manages batch creation, metadata, and QR code generation for supply chain tracking
 */
contract BatchContract is AccessControl {
    // Roles
    bytes32 public constant MANUFACTURER_ROLE = keccak256("MANUFACTURER_ROLE");
    bytes32 public constant SUPPLIER_ROLE = keccak256("SUPPLIER_ROLE");
    bytes32 public constant LOGISTICS_ROLE = keccak256("LOGISTICS_ROLE");

    // Batch counter
    uint256 private _batchIds;

    // Batch status enum
    enum BatchStatus {
        Created,
        InTransit,
        Delivered,
        Completed
    }

    // Batch structure
    struct Batch {
        uint256 id;
        string productType;
        uint256 quantity;
        address manufacturer;
        string origin;
        uint256 createdAt;
        BatchStatus status;
        string qrCode;
        string metadataURI; // IPFS hash for additional metadata
        bool exists;
    }

    // Mappings
    mapping(uint256 => Batch) public batches;
    mapping(string => uint256) public qrCodeToBatchId;
    mapping(address => uint256[]) public manufacturerBatches;

    // Events
    event BatchCreated(
        uint256 indexed batchId,
        address indexed manufacturer,
        string productType,
        uint256 quantity,
        string qrCode,
        uint256 timestamp
    );

    event BatchStatusUpdated(
        uint256 indexed batchId,
        BatchStatus newStatus,
        uint256 timestamp
    );

    event QRCodeScanned(
        uint256 indexed batchId,
        address indexed scanner,
        uint256 timestamp
    );

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    /**
     * @dev Creates a new batch with unique QR code
     * @param _productType Type of product
     * @param _quantity Quantity of items in batch
     * @param _origin Origin location
     * @param _metadataURI IPFS hash for additional metadata
     */
    function createBatch(
        string memory _productType,
        uint256 _quantity,
        string memory _origin,
        string memory _metadataURI
    ) external returns (uint256) {
        require(
            hasRole(MANUFACTURER_ROLE, msg.sender),
            "BatchContract: Caller is not a manufacturer"
        );
        require(_quantity > 0, "BatchContract: Quantity must be greater than 0");
        require(bytes(_productType).length > 0, "BatchContract: Product type required");

        _batchIds++;
        uint256 newBatchId = _batchIds;

        // Generate unique QR code
        string memory qrCode = _generateQRCode(newBatchId, msg.sender);

        // Create new batch
        batches[newBatchId] = Batch({
            id: newBatchId,
            productType: _productType,
            quantity: _quantity,
            manufacturer: msg.sender,
            origin: _origin,
            createdAt: block.timestamp,
            status: BatchStatus.Created,
            qrCode: qrCode,
            metadataURI: _metadataURI,
            exists: true
        });

        // Update mappings
        qrCodeToBatchId[qrCode] = newBatchId;
        manufacturerBatches[msg.sender].push(newBatchId);

        emit BatchCreated(
            newBatchId,
            msg.sender,
            _productType,
            _quantity,
            qrCode,
            block.timestamp
        );

        return newBatchId;
    }

    /**
     * @dev Scans QR code and returns batch information
     * @param _qrCode QR code to scan
     */
    function scanQRCode(string memory _qrCode) 
        external 
        returns (uint256) 
    {
        uint256 batchId = qrCodeToBatchId[_qrCode];
        require(batchId != 0, "BatchContract: Invalid QR code");

        emit QRCodeScanned(batchId, msg.sender, block.timestamp);
        return batchId;
    }

    /**
     * @dev Updates batch status
     * @param _batchId Batch ID to update
     * @param _newStatus New status
     */
    function updateBatchStatus(uint256 _batchId, BatchStatus _newStatus)
        external
    {
        require(
            hasRole(MANUFACTURER_ROLE, msg.sender) ||
            hasRole(SUPPLIER_ROLE, msg.sender) ||
            hasRole(LOGISTICS_ROLE, msg.sender),
            "BatchContract: Unauthorized"
        );
        require(batches[_batchId].exists, "BatchContract: Batch does not exist");

        batches[_batchId].status = _newStatus;

        emit BatchStatusUpdated(_batchId, _newStatus, block.timestamp);
    }

    /**
     * @dev Gets batch information by ID
     * @param _batchId Batch ID
     */
    function getBatchInfo(uint256 _batchId)
        external
        view
        returns (
            uint256 id,
            string memory productType,
            uint256 quantity,
            address manufacturer,
            string memory origin,
            uint256 createdAt,
            BatchStatus status,
            string memory qrCode,
            string memory metadataURI
        )
    {
        require(batches[_batchId].exists, "BatchContract: Batch does not exist");
        
        Batch memory batch = batches[_batchId];
        return (
            batch.id,
            batch.productType,
            batch.quantity,
            batch.manufacturer,
            batch.origin,
            batch.createdAt,
            batch.status,
            batch.qrCode,
            batch.metadataURI
        );
    }

    /**
     * @dev Gets batches by manufacturer
     * @param _manufacturer Manufacturer address
     */
    function getBatchesByManufacturer(address _manufacturer)
        external
        view
        returns (uint256[] memory)
    {
        return manufacturerBatches[_manufacturer];
    }

    /**
     * @dev Gets current batch count
     */
    function getCurrentBatchId() external view returns (uint256) {
        return _batchIds;
    }

    /**
     * @dev Validates batch integrity
     * @param _batchId Batch ID to validate
     */
    function validateBatchIntegrity(uint256 _batchId)
        external
        view
        returns (bool)
    {
        return batches[_batchId].exists && 
               batches[_batchId].quantity > 0 &&
               batches[_batchId].manufacturer != address(0);
    }

    /**
     * @dev Generates unique QR code for batch
     * @param _batchId Batch ID
     * @param _manufacturer Manufacturer address
     */
    function _generateQRCode(uint256 _batchId, address _manufacturer)
        private
        view
        returns (string memory)
    {
        return string(
            abi.encodePacked(
                "NEEV-",
                _toString(_batchId),
                "-",
                _toString(uint256(uint160(_manufacturer))),
                "-",
                _toString(block.timestamp)
            )
        );
    }

    /**
     * @dev Converts uint256 to string
     * @param value Value to convert
     */
    function _toString(uint256 value) private pure returns (string memory) {
        if (value == 0) {
            return "0";
        }
        uint256 temp = value;
        uint256 digits;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + uint256(value % 10)));
            value /= 10;
        }
        return string(buffer);
    }

    // Admin functions

    function grantManufacturerRole(address account) 
        public 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        _grantRole(MANUFACTURER_ROLE, account);
    }

    function grantSupplierRole(address account) 
        public 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        _grantRole(SUPPLIER_ROLE, account);
    }

    function grantLogisticsRole(address account) 
        public 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        _grantRole(LOGISTICS_ROLE, account);
    }
}

blockchain/contracts/NodeTrackingContrac.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title NodeTrackingContract
 * @dev Manages transit nodes and tracking points in the supply chain
 */
contract NodeTrackingContract is AccessControl {

    // Roles
    bytes32 public constant MANUFACTURER_ROLE = keccak256("MANUFACTURER_ROLE");
    bytes32 public constant SUPPLIER_ROLE = keccak256("SUPPLIER_ROLE");
    bytes32 public constant LOGISTICS_ROLE = keccak256("LOGISTICS_ROLE");
    bytes32 public constant NODE_OPERATOR_ROLE = keccak256("NODE_OPERATOR_ROLE");

    // Node counter
    uint256 private _nodeIds;

    // Node types
    enum NodeType {
        Origin,         // Starting point (manufacturer)
        Warehouse,      // Storage facility
        TransitHub,     // Logistics hub
        Supplier,       // Supplier facility
        Destination,    // Final destination
        ExitPoint       // Exit from supply chain
    }

    // Node status
    enum NodeStatus {
        Active,
        Inactive,
        Maintenance,
        Blocked
    }

    // Node structure
    struct Node {
        uint256 id;
        string name;
        string location;
        NodeType nodeType;
        NodeStatus status;
        address operator;
        uint256 createdAt;
        string coordinates; // GPS coordinates
        bool exists;
    }

    // Transit record structure
    struct TransitRecord {
        uint256 nodeId;
        uint256 batchId;
        uint256 timestamp;
        address scanner;
        string notes;
        bool isEntryPoint;
        bool isExitPoint;
        string verificationHash; // Hash for data verification
    }

    // Mappings
    mapping(uint256 => Node) public nodes;
    mapping(uint256 => TransitRecord[]) public batchTransitHistory;
    mapping(uint256 => mapping(uint256 => bool)) public batchAtNode; // batchId => nodeId => isPresent
    mapping(address => uint256[]) public operatorNodes;
    mapping(uint256 => uint256) public currentNodeLocation; // batchId => current nodeId

    // Events
    event NodeCreated(
        uint256 indexed nodeId,
        string name,
        string location,
        NodeType nodeType,
        address indexed operator,
        uint256 timestamp
    );

    event BatchEnteredNode(
        uint256 indexed batchId,
        uint256 indexed nodeId,
        address indexed scanner,
        uint256 timestamp,
        string notes
    );

    event BatchExitedNode(
        uint256 indexed batchId,
        uint256 indexed nodeId,
        address indexed scanner,
        uint256 timestamp
    );

    event NodeStatusUpdated(
        uint256 indexed nodeId,
        NodeStatus newStatus,
        uint256 timestamp
    );

    event TransitVerified(
        uint256 indexed batchId,
        uint256 indexed nodeId,
        string verificationHash,
        uint256 timestamp
    );

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    /**
     * @dev Creates a new node in the supply chain network
     */
    function createNode(
        string memory _name,
        string memory _location,
        NodeType _nodeType,
        address _operator,
        string memory _coordinates
    ) external onlyRole(DEFAULT_ADMIN_ROLE) returns (uint256) {
        require(bytes(_name).length > 0, "NodeTracking: Node name required");
        require(_operator != address(0), "NodeTracking: Invalid operator address");

        _nodeIds++;
        uint256 newNodeId = _nodeIds;

        nodes[newNodeId] = Node({
            id: newNodeId,
            name: _name,
            location: _location,
            nodeType: _nodeType,
            status: NodeStatus.Active,
            operator: _operator,
            createdAt: block.timestamp,
            coordinates: _coordinates,
            exists: true
        });

        operatorNodes[_operator].push(newNodeId);
        _grantRole(NODE_OPERATOR_ROLE, _operator);

        emit NodeCreated(
            newNodeId,
            _name,
            _location,
            _nodeType,
            _operator,
            block.timestamp
        );

        return newNodeId;
    }

    /**
     * @dev Records batch entry into a node
     */
    function recordBatchEntry(
        uint256 _batchId,
        uint256 _nodeId,
        string memory _notes
    ) external {
        require(nodes[_nodeId].exists, "NodeTracking: Node does not exist");
        require(
            hasRole(NODE_OPERATOR_ROLE, msg.sender) ||
            hasRole(LOGISTICS_ROLE, msg.sender) ||
            hasRole(MANUFACTURER_ROLE, msg.sender),
            "NodeTracking: Unauthorized scanner"
        );

        TransitRecord memory transit = TransitRecord({
            nodeId: _nodeId,
            batchId: _batchId,
            timestamp: block.timestamp,
            scanner: msg.sender,
            notes: _notes,
            isEntryPoint: true,
            isExitPoint: false,
            verificationHash: _generateVerificationHash(_batchId, _nodeId, block.timestamp)
        });

        batchTransitHistory[_batchId].push(transit);
        batchAtNode[_batchId][_nodeId] = true;
        currentNodeLocation[_batchId] = _nodeId;

        emit BatchEnteredNode(_batchId, _nodeId, msg.sender, block.timestamp, _notes);
        emit TransitVerified(_batchId, _nodeId, transit.verificationHash, block.timestamp);
    }

    /**
     * @dev Records batch exit from a node
     */
    function recordBatchExit(
        uint256 _batchId,
        uint256 _nodeId
    ) external {
        require(nodes[_nodeId].exists, "NodeTracking: Node does not exist");
        require(
            batchAtNode[_batchId][_nodeId],
            "NodeTracking: Batch not currently at this node"
        );

        TransitRecord memory transit = TransitRecord({
            nodeId: _nodeId,
            batchId: _batchId,
            timestamp: block.timestamp,
            scanner: msg.sender,
            notes: "Batch departed",
            isEntryPoint: false,
            isExitPoint: true,
            verificationHash: _generateVerificationHash(_batchId, _nodeId, block.timestamp)
        });

        batchTransitHistory[_batchId].push(transit);
        batchAtNode[_batchId][_nodeId] = false;
        
        if (nodes[_nodeId].nodeType == NodeType.ExitPoint) {
            currentNodeLocation[_batchId] = 0;
        }

        emit BatchExitedNode(_batchId, _nodeId, msg.sender, block.timestamp);
    }

    /**
     * @dev Gets complete transit history for a batch
     */
    function getTransitHistory(uint256 _batchId)
        external
        view
        returns (TransitRecord[] memory)
    {
        return batchTransitHistory[_batchId];
    }

    /**
     * @dev Gets current location of a batch
     */
    function getCurrentLocation(uint256 _batchId)
        external
        view
        returns (uint256 nodeId, string memory nodeName, string memory location)
    {
        uint256 currentNodeId = currentNodeLocation[_batchId];
        if (currentNodeId == 0) {
            return (0, "Unknown", "Unknown");
        }
        
        Node memory currentNode = nodes[currentNodeId];
        return (currentNodeId, currentNode.name, currentNode.location);
    }

    function _generateVerificationHash(
        uint256 _batchId,
        uint256 _nodeId,
        uint256 _timestamp
    ) private view returns (string memory) {
        bytes32 hash = keccak256(
            abi.encodePacked(
                _batchId,
                _nodeId,
                _timestamp,
                msg.sender,
                block.difficulty
            )
        );
        return _toHexString(hash);
    }

    function _toHexString(bytes32 _hash) private pure returns (string memory) {
        bytes memory alphabet = "0123456789abcdef";
        bytes memory str = new bytes(64);
        for (uint256 i = 0; i < 32; i++) {
            str[i*2] = alphabet[uint8(_hash[i] >> 4)];
            str[1+i*2] = alphabet[uint8(_hash[i] & 0x0f)];
        }
        return string(str);
    }

    // Admin functions
}

blockchain/contracts/SupplyChainContract.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "./BatchContract.sol";
import "./NodeTrackingContract.sol";

/**
 * @title SupplyChainContract
 * @dev Main contract that orchestrates the entire supply chain tracking system
 */
contract SupplyChainContract is AccessControl {
    
    // Contract references
    BatchContract public batchContract;
    NodeTrackingContract public nodeContract;

    // Roles
    bytes32 public constant MANUFACTURER_ROLE = keccak256("MANUFACTURER_ROLE");
    bytes32 public constant SUPPLIER_ROLE = keccak256("SUPPLIER_ROLE");
    bytes32 public constant LOGISTICS_ROLE = keccak256("LOGISTICS_ROLE");
    bytes32 public constant AUDITOR_ROLE = keccak256("AUDITOR_ROLE");

    // Supply chain journey structure
    struct SupplyChainJourney {
        uint256 batchId;
        uint256[] nodeSequence;
        uint256 startTime;
        uint256 endTime;
        bool isComplete;
        string finalDestination;
        mapping(uint256 => bool) requiredNodes;
    }

    // Certificate structure for compliance
    struct Certificate {
        string certificateType; // ISO, GST, BIS, MSME, etc.
        string certificateHash; // IPFS hash
        uint256 issuedDate;
        uint256 expiryDate;
        address issuer;
        bool isValid;
    }

    // Mappings
    mapping(uint256 => SupplyChainJourney) public supplyChainJourneys;
    mapping(uint256 => Certificate[]) public batchCertificates;
    mapping(address => Certificate[]) public entityCertificates;
    mapping(uint256 => string[]) public batchDocuments; // IPFS hashes
    mapping(uint256 => bool) public completedJourneys;

    // Events
    event SupplyChainJourneyStarted(
        uint256 indexed batchId,
        address indexed manufacturer,
        uint256 timestamp
    );

    event SupplyChainJourneyCompleted(
        uint256 indexed batchId,
        uint256 duration,
        uint256 timestamp
    );

    event CertificateAdded(
        uint256 indexed batchId,
        string certificateType,
        string certificateHash,
        uint256 timestamp
    );

    event DocumentAdded(
        uint256 indexed batchId,
        string documentHash,
        uint256 timestamp
    );

    event ComplianceAlertTriggered(
        uint256 indexed batchId,
        string alertType,
        string message,
        uint256 timestamp
    );

    constructor(address _batchContract, address _nodeContract) {
        batchContract = BatchContract(_batchContract);
        nodeContract = NodeTrackingContract(_nodeContract);
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    /**
     * @dev Creates a complete supply chain journey for a batch
     */
    function createSupplyChainJourney(
        string memory _productType,
        uint256 _quantity,
        string memory _origin,
        string memory _metadataURI,
        string memory _finalDestination,
        uint256[] memory _requiredNodeIds
    ) external returns (uint256) {
        require(
            hasRole(MANUFACTURER_ROLE, msg.sender),
            "SupplyChain: Only manufacturers can create journeys"
        );

        // Create batch first
        uint256 batchId = batchContract.createBatch(
            _productType,
            _quantity,
            _origin,
            _metadataURI
        );

        // Initialize supply chain journey
        SupplyChainJourney storage journey = supplyChainJourneys[batchId];
        journey.batchId = batchId;
        journey.startTime = block.timestamp;
        journey.isComplete = false;
        journey.finalDestination = _finalDestination;

        // Set required nodes
        for (uint256 i = 0; i < _requiredNodeIds.length; i++) {
            journey.requiredNodes[_requiredNodeIds[i]] = true;
        }

        emit SupplyChainJourneyStarted(batchId, msg.sender, block.timestamp);

        return batchId;
    }

    /**
     * @dev Updates batch location and validates supply chain rules
     */
    function updateBatchLocation(
        uint256 _batchId,
        uint256 _nodeId,
        string memory _notes
    ) external {
        require(
            hasRole(LOGISTICS_ROLE, msg.sender) ||
            hasRole(MANUFACTURER_ROLE, msg.sender),
            "SupplyChain: Unauthorized"
        );

        // Record in node tracking
        nodeContract.recordBatchEntry(_batchId, _nodeId, _notes);

        // Update journey sequence
        SupplyChainJourney storage journey = supplyChainJourneys[_batchId];
        journey.nodeSequence.push(_nodeId);

        // Check if this completes the journey
        _checkJourneyCompletion(_batchId);
    }

    /**
     * @dev Marks exit from a node
     */
    function markExitNode(uint256 _batchId, uint256 _nodeId) 
        external 
    {
        require(
            hasRole(LOGISTICS_ROLE, msg.sender) ||
            hasRole(MANUFACTURER_ROLE, msg.sender),
            "SupplyChain: Unauthorized"
        );

        nodeContract.recordBatchExit(_batchId, _nodeId);

        // Check if journey is complete
        _checkJourneyCompletion(_batchId);
    }

    /**
     * @dev Adds a certificate to a batch
     */
    function addBatchCertificate(
        uint256 _batchId,
        string memory _certificateType,
        string memory _certificateHash,
        uint256 _expiryDate
    ) external {
        require(
            hasRole(MANUFACTURER_ROLE, msg.sender) ||
            hasRole(SUPPLIER_ROLE, msg.sender),
            "SupplyChain: Unauthorized"
        );

        Certificate memory cert = Certificate({
            certificateType: _certificateType,
            certificateHash: _certificateHash,
            issuedDate: block.timestamp,
            expiryDate: _expiryDate,
            issuer: msg.sender,
            isValid: true
        });

        batchCertificates[_batchId].push(cert);

        emit CertificateAdded(_batchId, _certificateType, _certificateHash, block.timestamp);
    }

    /**
     * @dev Adds a document to a batch (IPFS hash)
     */
    function addBatchDocument(
        uint256 _batchId,
        string memory _documentHash
    ) external {
        require(
            hasRole(MANUFACTURER_ROLE, msg.sender) ||
            hasRole(SUPPLIER_ROLE, msg.sender) ||
            hasRole(LOGISTICS_ROLE, msg.sender),
            "SupplyChain: Unauthorized"
        );

        batchDocuments[_batchId].push(_documentHash);

        emit DocumentAdded(_batchId, _documentHash, block.timestamp);
    }

    /**
     * @dev Gets complete supply chain history for a batch
     */
    function getSupplyChainHistory(uint256 _batchId)
        external
        view
        returns (
            uint256[] memory nodeSequence,
            uint256 startTime,
            uint256 endTime,
            bool isComplete,
            string memory finalDestination
        )
    {
        SupplyChainJourney storage journey = supplyChainJourneys[_batchId];
        return (
            journey.nodeSequence,
            journey.startTime,
            journey.endTime,
            journey.isComplete,
            journey.finalDestination
        );
    }

    /**
     * @dev Gets all certificates for a batch
     */
    function getBatchCertificates(uint256 _batchId)
        external
        view
        returns (Certificate[] memory)
    {
        return batchCertificates[_batchId];
    }

    /**
     * @dev Gets all documents for a batch
     */
    function getBatchDocuments(uint256 _batchId)
        external
        view
        returns (string[] memory)
    {
        return batchDocuments[_batchId];
    }

    /**
     * @dev Validates complete batch integrity across all contracts
     */
    function validateBatchIntegrity(uint256 _batchId)
        external
        view
        returns (bool isValid, string memory reason)
    {
        // Check batch exists and is valid
        if (!batchContract.validateBatchIntegrity(_batchId)) {
            return (false, "Batch integrity failed");
        }

        // Check transit sequence validity
        // Note: Simplified version - in production, you'd call nodeContract validation
        SupplyChainJourney storage journey = supplyChainJourneys[_batchId];
        if (journey.nodeSequence.length == 0) {
            return (false, "No transit records");
        }

        // Check certificate validity
        Certificate[] memory certs = batchCertificates[_batchId];
        for (uint256 i = 0; i < certs.length; i++) {
            if (certs[i].expiryDate < block.timestamp) {
                return (false, "Expired certificate found");
            }
        }

        return (true, "Batch integrity valid");
    }

    /**
     * @dev Triggers compliance alerts for expired certificates
     */
    function checkComplianceAlerts(uint256 _batchId) external {
        Certificate[] memory certs = batchCertificates[_batchId];
        
        for (uint256 i = 0; i < certs.length; i++) {
            // Check if certificate expires within 30 days
            if (certs[i].expiryDate <= block.timestamp + 30 days && 
                certs[i].expiryDate > block.timestamp) {
                
                emit ComplianceAlertTriggered(
                    _batchId,
                    "CERTIFICATE_EXPIRING",
                    string(abi.encodePacked("Certificate ", certs[i].certificateType, " expires soon")),
                    block.timestamp
                );
            }
            
            // Check if certificate is already expired
            if (certs[i].expiryDate <= block.timestamp) {
                emit ComplianceAlertTriggered(
                    _batchId,
                    "CERTIFICATE_EXPIRED",
                    string(abi.encodePacked("Certificate ", certs[i].certificateType, " has expired")),
                    block.timestamp
                );
            }
        }
    }

    /**
     * @dev Gets real-time tracking information
     */
    function getRealTimeTracking(uint256 _batchId)
        external
        view
        returns (
            uint256 currentNodeId,
            string memory currentLocation,
            uint256 lastUpdateTime,
            string memory status
        )
    {
        (currentNodeId, , currentLocation) = nodeContract.getCurrentLocation(_batchId);
        
        // Get batch status
        (, , , , , , BatchContract.BatchStatus batchStatus, ,) = batchContract.getBatchInfo(_batchId);
        
        string memory statusString;
        if (batchStatus == BatchContract.BatchStatus.Created) statusString = "Created";
        else if (batchStatus == BatchContract.BatchStatus.InTransit) statusString = "In Transit";
        else if (batchStatus == BatchContract.BatchStatus.Delivered) statusString = "Delivered";
        else if (batchStatus == BatchContract.BatchStatus.Completed) statusString = "Completed";

        return (currentNodeId, currentLocation, block.timestamp, statusString);
    }

    /**
     * @dev Internal function to check if journey is complete
     */
    function _checkJourneyCompletion(uint256 _batchId) private {
        SupplyChainJourney storage journey = supplyChainJourneys[_batchId];
        
        // Simple completion logic - can be enhanced
        if (journey.nodeSequence.length > 0 && !journey.isComplete) {
            // Check if we've reached final destination or exit point
            uint256 lastNodeId = journey.nodeSequence[journey.nodeSequence.length - 1];
            
            // This would need to check node type from NodeTrackingContract
            // For now, we'll mark complete after 3+ nodes
            if (journey.nodeSequence.length >= 3) {
                journey.isComplete = true;
                journey.endTime = block.timestamp;
                completedJourneys[_batchId] = true;

                uint256 duration = journey.endTime - journey.startTime;
                emit SupplyChainJourneyCompleted(_batchId, duration, block.timestamp);
            }
        }
    }

    // Admin functions

    function grantManufacturerRole(address account) 
        public 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        _grantRole(MANUFACTURER_ROLE, account);
        batchContract.grantManufacturerRole(account);
    }

    function grantSupplierRole(address account) 
        public 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        _grantRole(SUPPLIER_ROLE, account);
        batchContract.grantSupplierRole(account);
    }

    function grantLogisticsRole(address account) 
        public 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        _grantRole(LOGISTICS_ROLE, account);
        batchContract.grantLogisticsRole(account);
    }
}

